import React,{useEffect,useState} from "react";
import { useNavigate } from "react-router";

const JobProfile = ({fetchJob,fetchJobApp,storage}) => {
    const [job,setJob] = useState('');
    const [jobTitle,setJobTitle] = useState('');
    const [jobDescription,setJobDescription] = useState('');
    const [jobType,setJobType] = useState('');
    const [jobSkills,setJobSkills] = useState('');
    const [company,setCompany] = useState('');
    const navigate = useNavigate('');

    useEffect(()=> {
        getJob();
    },[]);
    const getJob = async() => {
        const response = await fetchJob(storage.getItem("jobId"));
        if (response.id){
            setJob(response);
        }
        else{
            alert("Couldn't load job");
            return;
        }
    }

    const loadJobApp = async() => {
        const response = await fetchJobApp(job.id);
        if (response){
            navigate(`/signed-in/apply-job/`);
        }
    }

    return (
        <>
        <div>
        <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
        <div>
        <h3 className="job-page-header">Company Name</h3>
        <h4 >{job.company_name}</h4>
        </div>
        <div>
        <h3 className="job-page-header">Job Title</h3>
        <h3>{job.title}</h3>
        </div>
        <div>
        <div>
        <h3 className="job-page-header">Salary</h3>
        <h4>${job.salary}/hr</h4>
        </div>
        <div>
        <h3 className="job-page-header">Job Hours</h3>
        <h4>{job.job_hours}</h4>
        </div>
        <div>
        <h3 className="job-page-header">Job Type</h3>
        <h4>{job.job_type}</h4>
        </div>
        </div>
        <div>
        <form>


        </form>
        </div>
        </div>
        </>
    )

}

export default JobProfile;
