import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router";
import "./childComponents.css";
import PostModal from "./PostModal";


const UserHome = ({createPost,fetchPosts,storage}) => {
    const [showModal,setModal] = useState(false);
    const [postContent,setPostContent] = useState("");
    const [posts,setPosts] = useState([]);
    const [fullName,setFullName] = useState("");
    const [firstname,setFirstName] = useState('');
    const [last_name,setLastName] = useState('');
    const navigate = useNavigate();

    const openModal = () => {setModal(true)};
    const closeModal = () => {setModal(false)};

    useEffect(()=> {
        getAllPosts();
    },[]);

    const getAllPosts = async() => {

        const postsData = await fetchPosts();
        setPosts(postsData);
    }

    const handleCreatePost = async(e) => {
        e.preventDefault();
        if (!postContent.trim()){
            alert("Post can't be empty");
            return;
        }

        const postData = {
            content: postContent.trim(),
        }
        const response = await createPost(postData);
        if (response){
            closeModal();
        }
        console.log("Here's what the backend response says: ",response);

    }

    const handleSearchUser = (e) => {
        e.preventDefault();

        const trimmed = value.trim();
        const spaceIdx = trimmed.indexOf(" ");

        if (spaceIdx !== -1){
            setfirstname(trimmed.slice(0,spaceIdx));

        }
    }

    const handleSignOut = (e) => {
        e.preventDefault();
        storage.removeItem("authToken");
        navigate("/");
    }

    return (
        <>
        <div>
        <div>
        <input onClick={openModal}
        className="text-user-box-post"
        type="text"
        placeholder="Share A Post"/>
        <PostModal isOpen={showModal} onClose={closeModal}>
        <h2>What's on your mind?</h2>
        <textarea
        type="text"

        placeholder="What do you want to post?"
        rows="5"
        value={postContent}
        onChange={(e) => setPostContent(e.target.value)}
        style={{ width: '100%', marginBottom: '10px', minHeight: "150px" }}
        />
            <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px' }}>
            <button onClick={closeModal}>Cancel</button>
            <form onSubmit={handleCreatePost}>
                <button type="submit">Share Post</button>
            </form>
            </div>
        </PostModal>
        {/* {!showModal && (
        <button onClick={(e) => setModal(true)}>Share A Post</button>
        )} */}
        </div>
        {/* {showModal && (
            <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center">
                <div className="bg-white rounded-lg shadow-lg w-96 p-6 relative">
                <button onClick={(e) => setModal(false)}>Share A Post</button>
                <h2>Share A Post</h2>
                <textarea
                className="w-full h-24 border rounded p-2"
                type="text"
                placeholder="Share"
                />
                <button>Make Post</button>
                </div>
            </div>
        )} */}
        <div>
        {posts.map((post) => (
            <li key={post.date_posted} className="post-content-list-home">
                <p className="post-content-home">
                <button className="post-button-author-home">
                <text className="post-text-author-home">{post.author_first_name} {post.author_last_name} </text>
                </button>
                <text className="post-content-body" >{post.content}</text>
                </p>
            </li>
        ))}
        </div>
        {/* <button onClick={(e) => navigate("/") } className="back-button">Back</button> */}
        <form onSubmit={handleSignOut}>
        <button type="submit" className="sign-out-button">Sign Out</button>
        </form>
        <input
        className="home-user-search-button"
        type="text"
        placeholder="Search 🔍
        "/>
        {/* <h1>Account & Profile Successfully Created </h1> */}
        </div>
        </>
    )
}

export default UserHome;
