import React,{useState,useEffect} from "react";

const JobAppPage = ({fetchQuestions,storage}) => {
    const [questions,setQuestions] = useState([]);
    const [questionTypes,setQuestionTypes] = useState([]);
    const [questionAnswers,setQuestionAnswers] = useState([]);
    const [currentPage,setCurrentPage] = useState(1);
    const questionsperPage = 4;
    const [currentQuestions,setCurrentQuestions] = useState([]);
    // const indexofLastQuestion = currentPage * questionsperPage;
    // const indexofFirsttQuestion = indexofLastQuestion- questionsperPage
    // const sliced = questions.slice(indexofFirsttQuestion,indexofLastQuestion);

    useEffect(() => {
        if (!questions || questions.length === 0) return; // ðŸ‘ˆ guard against undefined or empty

        const indexofLastQuestion = currentPage * questionsperPage;
        const indexofFirstQuestion = indexofLastQuestion - questionsperPage;
        const sliced = questions.slice(indexofFirstQuestion, indexofLastQuestion);
        setCurrentQuestions(sliced);
    }, [questions, currentPage]);

    const loadQuestions = async() => {
        const response = await fetchQuestions(storage.getItem("jobId"));
        setQuestions(response.questions);
        setQuestionTypes(response.question_types);
        setQuestionAnswers(response.answers);
    }

    return (
        <>
        <div>
        <ol>
        {currentQuestions.map((question,idx) => (
            <li key={idx}>{question}</li>
        ))}
        </ol>
        {/* <button onClick={(e) => setCurrentPage()}></button> */}
        </div>
        </>
    )
}

export default JobAppPage;
