import React,{useState} from "react";
import { useNavigate } from "react-router";
import { useSearchParams } from "react-router-dom";

const AccountVerify = ({storage}) =>{
    const navigate = useNavigate();
    const [searchParams] = useSearchParams();
    const token = searchParams.get('token');

    const handleVerify = async(e) => {
        e.preventDefault();
        const response = await fetch(`http://localhost:8000/register/verify-email?token=${token}`,{
            method: "POST",
            headers: {
                "Content-Type":"applications/json",
                "Authorization": `Bearer ${token}`
            },
        });
        const responseData = await response.json();
        storage.setItem("authToken",responseData.access_token);
        storage.setItem("verifiedUserId",responseData.account_id);

        navigate("/register/profile/");
    }
    return (
        <>
        <form onSubmit={handleVerify}>
        <div>
        <button className="verify-email-button" type="submit">Verify Email</button>
        </div>
        </form>
        </>
    )
}

export default AccountVerify;
