import AccountRegister from "./ChildComponents/AccountRegister";
import AccountRegisterAwait from "./ChildComponents/AccountRegisterAwait";
import AccountVerify from "./ChildComponents/AccountVerify";
import UserProfileRegistration from "./ChildComponents/UserProfileRegistration";
import LoginAccount from "./ChildComponents/LoginAccount";
import UserHome from "./ChildComponents/UserHome";
import HomeScreen from "./ChildComponents/HomeScreen";
import UserSearchHome from "./ChildComponents/HomeScreenPages/UserSearchHome";
import MiniQuizHome from "./ChildComponents/HomeScreenPages/MiniQuizHome";
import QuizTree from "./ChildComponents/QuizTree";
import EngineeringPage from "./ChildComponents/HomeScreenPages/FieldPages/EngineeringPage";
import SearchPage from "./ChildComponents/SearchPage";
import OtherUserProfile from "./ChildComponents/OtherUserProfile";
import CompanyProfileRegistration from "./ChildComponents/CompanyRegistration";
import AllUserCompanies from "./ChildComponents/AllUserCompanies";
import CompanyProfile from "./ChildComponents/CompanyProfile";
import AllJobsUserProfile from "./ChildComponents/AllJobsUserProfile";
import PostNewJob from "./ChildComponents/PostNewJob";
import React,{useState,useEffect} from "react";
import { BrowserRouter,Routes,Route } from "react-router-dom";
import { useNavigate } from "react-router";

const ComponentsLists = () => {

    const [accounts,setAccounts] = useState([]);

    const fetchUser = async(userId) => {
        try{
            const response = await fetch(`http://localhost:8000/users/${userId}`,{
                method:"GET",
                headers: {
                    "Content-Type": "application/json",
                },
            })
            if (!response.ok){
                return;
            }
            const responseData = await response.json();
            return responseData;
        }
        catch(error){
            console.log("Error fetching user",error);
            alert(`Could not fetch the user ${error}`);
        }
    }

    const fetchPostUser = async(userId) => {
        try{
            const response = await fetch(`http://localhost:8000/users/posts/${userId}`,{
                method:"GET",
                headers: {
                    "Content-Type": "application/json",
                },
            })
            if (!response.ok){
                return;
            }
            const responseData = await response.json();
            return responseData;
        }
        catch(error){
            console.log("Error fetching user",error);
            alert(`Could not fetch the user ${error}`);
        }
    }

    const userSearch = async(firstName,lastName) => {
        try{
        // const response = await fetch(`http://localhost:8000/home/users?first_name=${first_name_user}&last_name=${last_name_user}`)
        const response = await fetch(`http://localhost:8000/home/users?first_name=${firstName}&last_name=${lastName}`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            }
        })
        if (!response.ok){
            return;
        }
        const responseData = response.json();
        return responseData;
        }
        catch(error){
            console.log("Error fetching user",error);
            alert(`Could not fetch the user ${error}`);
        }

    }

    const jobSearch = async(firstName,lastName) => {
        try{
        // const response = await fetch(`http://localhost:8000/home/users?first_name=${first_name_user}&last_name=${last_name_user}`)
        const response = await fetch(`http://localhost:8000/home/jobs?first_name=${firstName}&last_name=${lastName}`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            }
        })
        if (!response.ok){
            return;
        }
        const responseData = response.json();
        return responseData;
        }
        catch(error){
            console.log("Error fetching user",error);
            alert(`Could not fetch the user ${error}`);
        }

    }

    const fetchAllJobs = async() => {
        // const token = localStorage.getItem("authToken");
        const response = await fetch("http://localhost:8000/home/jobs/all/",{
            method: "GET",
            headers:{
                "Content-Type":"application/json",
            },
        })
        const responseData = await response.json();
        return responseData
    }

    const companySearch = async(firstName,lastName) => {
        try{
        // const response = await fetch(`http://localhost:8000/home/users?first_name=${first_name_user}&last_name=${last_name_user}`)
        const response = await fetch(`http://localhost:8000/home/companies?first_name=${firstName}&last_name=${lastName}`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            }
        })
        if (!response.ok){
            return;
        }
        const responseData = response.json();
        return responseData;
        }
        catch(error){
            console.log("Error fetching user",error);
            alert(`Could not fetch the user ${error}`);
        }

    }

    const fetchUserCompany = async(companyId) => {
        const token = localStorage.getItem("authToken");
        const response = await fetch(`http://localhost:8000/home/user-companies/${companyId}`,{
            method: "GET",
            headers:{
                "Content-Type":"application/json",
                "Authorization": `Bearer ${token}`
            },
        })
        const responseData = await response.json();
        return responseData;
    }

    const fetchUserCompanies = async() => {
        const token = localStorage.getItem("authToken");
        const response = await fetch("http://localhost:8000/home/user-companies/",{
            method: "GET",
            headers:{
                "Content-Type":"application/json",
                "Authorization": `Bearer ${token}`
            },
        })
        const responseData = await response.json();
