import AccountRegister from "./ChildComponents/AccountRegister";
import AccountRegisterAwait from "./ChildComponents/AccountRegisterAwait";
import AccountVerify from "./ChildComponents/AccountVerify";
import React,{useState,useEffect} from "react";
import { BrowserRouter,Routes,Route } from "react-router-dom";
import { useNavigate } from "react-router";

const ComponentsLists = () => {

    const [accounts,setAccounts] = useState([]);

    const createAccount = async(userInfo) => {
        try{
            console.log("Payload sent");
            const response = await fetch("http://localhost:8000/register/", {
            method: "POST",
            headers: {
                "Content-Type":"application/json"
            },
            body: JSON.stringify(userInfo),
        });
        console.log(response);
        // if (!response.ok){
        //     alert("Username or Email already exist, Please a choose a different one");
        //     return;
        // }

        if (!response.ok){
            // const errorData = response.json().catch();
            // console.log(`Backend Error ${response.status}:`,errorData);
            // throw new Error(`HTTP Error: ${response.status} - ${JSON.stringify(errorData)}`);
            return response.text().then(text => {
                throw new Error(`HTTP error! status: ${response.status}, response: ${text}`)});
        }

        const newAccount = await response.json();
        setAccounts((oldAccounts) => [...oldAccounts,newAccount])
        localStorage.setItem("currUserId",)
        return newAccount;

    }
    catch(error){
        console.log(`Couldn't create new Account: ${error}`);
        alert(`Account couldn't be created ${error}`);
        return;
    }

    const verifyAccount = async(token) =>{
        const response = await fetch(`http://localhost:8000/register/verify-email?token=${token}`, {
            method: "POST",
            headers: {
                "Content-Type":"application/json",
            },
            body: JSON.stringify(token),
        })
    }

    }
    return (
        <>
        <div>
        <Routes>
        <Route path="/register/" element={<AccountRegister createAccount={createAccount}/>}/>
        <Route path="/register/await" element={<AccountRegisterAwait/>}/>
        <Route path="/register/verify-email" element={<AccountVerify storage={localStorage}/>}/>
        </Routes>
        </div>
        </>
    )
}

export default ComponentsLists;
