import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router";
import "./childComponents.css";

const FORM_STAGES = {
    PAGEONE: "pageone",
    PAGETWO: "pagetwo",
    PAGETHREE: "pagethree",
    PAGEFOUR: "pagefour",
    PAGEFIVE: "pagefive",
    // PAGESIX: "pagesix",
    // PAGEONE: "pageone",
    // PAGEONE: "pageone",
    // PAGEONE: "pageone",

}

const MiniQuizHome = ({node}) => {
    const [currentQuestionId, setCurrentQuestionId] = useState("q1"); // Start at the first question
    const [currentQuestion, setCurrentQuestion] = useState(null);
    const [quizResult, setQuizResult] = useState(null); // To store the final result string
    const [selectedAnswerId, setSelectedAnswerId] = useState(null); // To track the currently selected radio button

    const getNodeById = (id) => {
    return quizData.find(node => node.id === id);
    };

    useEffect(() => {
        // Find the current node (question or result) based on currentQuestionId
        const node = getNodeById(currentQuestionId);
        if (node) {
            if (node.question) { // It's a question node
                setCurrentQuestion(node);
                setQuizResult(null); // Clear result if navigating back to a question
                setSelectedAnswerId(null); // Reset selected answer for new question
            } else if (node.result) { // It's a result node
                setQuizResult(node.result);
                setCurrentQuestion(null); // Hide question UI
            }
        } else {
            console.error("Error: Node not found for ID:", currentQuestionId);
            // Optionally, handle error by showing an error message or restarting
            setQuizResult("An error occurred. Please restart the quiz.");
            setCurrentQuestion(null);
        }
    }, [currentQuestionId]); // Re-run effect when currentQuestionId changes

    // Handles when a user selects a radio button
    const handleRadioChange = (uniqueAnswerId) => {
        setSelectedAnswerId(uniqueAnswerId);
    };

    // Handles when the user clicks the "Next" button
    const handleSubmitAnswer = () => {
        if (selectedAnswerId === null) {
            alert("Please select an answer before proceeding!");
            return;
        }

        // Find the current question object
        const question = getNodeById(currentQuestionId);
        if (!question || !question.answers) {
            console.error("Current question or its answers are undefined.", question);
            alert("An internal error occurred. Please try again.");
            return;
        }

        // Find the selected answer object from the current question's answers
        const selectedAnswer = question.answers.find(
            (ans, index) => `${question.id}-${index}` === selectedAnswerId
        );

        if (selectedAnswer) {
            if (selectedAnswer.nextQuestionId) {
                setCurrentQuestionId(selectedAnswer.nextQuestionId);
            } else if (selectedAnswer.resultId) {
                // If it's a result, update the currentQuestionId to point to the result node
                // The useEffect will then pick this up and display the result.
                setCurrentQuestionId(selectedAnswer.resultId);
            }
        } else {
            console.error("Selected answer not found for ID:", selectedAnswerId);
            alert("Please select a valid answer.");
        }
    };

    const restartQuiz = () => {
        setCurrentQuestionId("q1");
        setQuizResult(null);
        setSelectedAnswerId(null);
    };

    // --- Render Logic ---
    return (
        <div className="quiz-container">
            {quizResult ? (
                // Display Quiz Result
                <div className="quiz-result-section">
                    <h2>Your Recommended Role:</h2>
                    <p className="quiz-result">{quizResult}</p>
                    <button className="quiz-button" onClick={restartQuiz}>Start Over</button>
                </div>
            ) : (
                // Display Current Question
                currentQuestion ? (
                    <div className="quiz-question-section">
                        <h2>{currentQuestion.question}</h2>
                        <div className="quiz-options">
                            {currentQuestion.answers.map((answer, index) => {
                                const uniqueAnswerId = `${currentQuestion.id}-${index}`; // Unique ID for each radio option
                                return (
                                    <div key={uniqueAnswerId} className="quiz-option-item">
                                        <input
                                            type="radio"
                                            id={uniqueAnswerId}
                                            name={`question-${currentQuestion.id}`} // Group radio buttons by question
                                            value={uniqueAnswerId}
                                            checked={selectedAnswerId === uniqueAnswerId}
                                            onChange={() => handleRadioChange(uniqueAnswerId)}
                                        />
                                        <label htmlFor={uniqueAnswerId}>{answer.text}</label>
                                    </div>
                                );
                            })}
                        </div>
                        <button
                            className="quiz-button"
                            onClick={handleSubmitAnswer}
                            disabled={selectedAnswerId === null} // Disable if no answer is selected
                        >
                            Next
                        </button>
                    </div>
                ) : (
                    // Loading or error state
                    <div>Loading quiz...</div>
                )
            )}
        </div>
    );
}

export default MiniQuizHome;
