import React,{useEffect, useState} from "react";
import { useNavigate, useParams } from "react-router";
import image from "./../../images/profile_pic.png"
import "./childComponents.css";

const OtherUserProfile = ({sendRequest, getUser,storage,getPosts,updateConnection,cancelConnection}) => {
    const [otherUser,setOtherUser] = useState("");
    const [otherUserSkills,setOtherUserSkills] = useState([]);
    const [otherUserExperience,setOtherUserExperience] = useState([]);
    const [posts,setPosts] = useState([]);
    const [connect,setConnect] = useState(false);
    const [updateConnectionStatus,setUpdateConnectionStatus] = useState('');
    const { otherUserId } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        loadUser()
},[])
    useEffect(() => {
        loadUserPosts()
    },[])

        const loadUser = async() => {
            const response = await getUser((storage.getItem("otherUser")));
            console.log(response.status)
            console.log(response)
            if (response.user){
                setOtherUser(response.user);
                setOtherUserSkills(response.skills);
                setOtherUserExperience(response.work)
            }
            else{
                console.log("Bad Response: ",response);
            }
            // storage.setItem("otherUser",response);
            // setOtherUser(storage.getItem("otherUser"));
            // console.log("Other user: ",otherUser);
        }

        const loadUserPosts = async() => {
            const response = await getPosts((storage.getItem("otherUser")));
            console.log(response)
            if (response){
                setPosts(response);
            }
            else{
                console.log("Bad Response: ",response);
            }
            // storage.setItem("otherUser",response);
            // setOtherUser(storage.getItem("otherUser"));
            // console.log("Other user: ",otherUser);
        }

        const handleRequest = async() => {
            const response = await sendRequest(otherUser.user_id);
            if (response === null){
                alert("This user is already your friend");
                return;
            }
            // The response from the API should indicate the status of the request.
            // For example, if the request is successful, it returns an id.
            if (response.id){
                alert("Friend Request sent");
                setOtherUser({...otherUser, status: "pending", user_one_id: "true"});
            }
            console.log("An error occured ",response);
        }

        const handleConnectionButton = async(updateStatus) => {
            const requestData = {
                user_id:otherUser.user_id,
                status: updateStatus.trim()
            }
            const response = await updateConnection(requestData);
            // Assuming the API call is successful, update the local state.
            if (response) {
                setOtherUser({...otherUser, status: updateStatus.trim()});
            }
        }

        const handleCancelButton = async() => {
            const response = await cancelConnection(otherUser.user_id);
        }

        return (
            <>
            <div>
                <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
                <div className="other-profile-name-profile-pic-container">
                <img className="other-profile-image-screen" src={image}></img>
                <h2>{otherUser.first_name} {otherUser.last_name}</h2>
                <h2 className="user-profile-location-screen">{otherUser.location_state}, {otherUser.location_country}</h2>
                </div>
                <h1 className="other-user-bio-header-profile">About:</h1>
                <h2 className="other-user-bio-profile">{otherUser.bio}</h2>
                {otherUser.no_connection === "" && connect === false && (
                <button onClick={handleRequest} className="other-user-connection-button-profile" >🙋🏿‍♂️ Connect</button>
                )}
                {otherUser.no_connection === "pending" && connect === true && (
                    <div>
                    <button onClick={handleCancelButton} className="other-user-connection-button-profile" >Cancel ❌</button>
                    <button onClick={handleRequest} className="other-user-connection-button-profile" disabled>Pending</button>
                    </div>
                )}
                {otherUser.status === "pending" && otherUser.user_one_id === "true" && (
                    <div>
                    <button onClick={handleCancelButton} className="other-user-connection-button-profile" >Cancel ❌</button>
                    <button onClick={handleRequest} className="other-user-connection-button-profile" disabled>Pending</button>
                    </div>
                )}
                {otherUser.status === "pending" && otherUser.user_one_id === "false" && (
                    <div>
                    <h4>{otherUser.first_name} wants to connect</h4>
                    <button onClick={() => handleConnectionButton("accepted")} className="other-user-connection-button-profile" >Accept 👍</button>
                    <button onClick={() => handleConnectionButton("rejected")} className="other-user-connection-button-profile">Reject 👎</button>
                    </div>
                )}
                {otherUser.status === "accepted" && (
                <button onClick={handleRequest} className="other-user-connection-button-profile">✅ Accepted</button>
                )}
                {otherUser.status === "rejected" && (
                <button onClick={handleRequest} className="other-user-connection-button-profile">🙅‍♂️ Rejected</button>
                )}
                <h3>Work Experience</h3>
                {Array.isArray(otherUserExperience) && (
                <div>
                {otherUserExperience.map((work)=>(
                    <div>
                    {/* <button>{work.skill_name}</button> */}
                    <p>
                    <h3> {work.title}</h3>
                    <h6>{work.company_name} - {work.job_type}</h6>
                    <h6>{work.location_state}, {work.location_country}</h6>
                    <h5>Description:</h5>
                    <h6>{work.description}</h6>
                    </p>
                    </div>
                ))}
                </div>
                )}

                <h3>Skills</h3>
                {Array.isArray(otherUserSkills) && (
                <div>
                {otherUserSkills.map((skill)=>(
                    <div>
                    <button>{skill.skill_name}</button>
                    </div>
                ))}
                </div>
                )}
                <h3>Activity:</h3>
                {posts.map((post)=> (
                    <li key={post.id} className="post-content-list-home">
                        <p className="post-content-home">
                            <button className="post-button-author-home">
                            <text className="post-text-author-home" onClick={(e)=>getUser(post.author_id)}>{post.author_first_name} {post.author_last_name} posted this</text>
                            </button>
                            <text className="post-content-body" >{post.content}</text>
                            {/* <text className="post-content-time" >{post.date_posted}</text> */}
                        </p>
                    </li>
                ))}
            </div>
            </>
        )

}

export default OtherUserProfile;
