import React,{useEffect, useState} from "react";
import { useNavigate } from "react-router-dom";
import "./childComponents.css";

// import { useSearchParams } from "react-router-dom";

const AllUserCompanies = ({fetchCompanies, loadCompany,storage}) => {
    const [userCompanies, setUserCompanies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        getCompanies();
    }, []);

    const getCompanies = async () => {
        try {
            setLoading(true);
            const response = await fetchCompanies();
            console.log(response);
            // Ensure response is an array before setting state
            if (Array.isArray(response)) {
                setUserCompanies(response);
            } else {
                setUserCompanies([]);
                setError("Received invalid data format");
            }
        } catch (err) {
            setError(err.message);
            setUserCompanies([]);
        } finally {
            setLoading(false);
        }
    };

    const handleCompanySelect = async(companyId) =>{
        const response = await loadCompany(companyId);

        if (response.company_name){
            navigate(`/signed-in/feed/user-companies/${companyId}`);
        }
    }

    if (loading) return <div>Loading companies...</div>;
    if (error) return <div>Error: {error}</div>;



    return (
        <div>
            <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
            <h1>Companies</h1>
            {userCompanies.length > 0 ? (
                <ul>
                    {userCompanies.map((company, index) => (
                        <li key={index} className="list-of-user-companies">
                            {/* <form onSubmit={handleCompanySelect(company.company_id)}> */}
                            <button className="button-for-each-user-companies" type="submit" onClick={(e) => handleCompanySelect(company.company_id)}>
                                <h2 className="button-company-header-for-each-user-companies">Company name:</h2>
                                <h3>{company.company_name}</h3> 
                                <h2 className="button-company-industry-for-each-user-companies">Description:</h2> 
                                <h3>{company.description}</h3>  
                                <h2 className="button-date-company-for-each-user-companies">Date Created:</h2>
                                <h3>{company.date_created}</h3>
                            </button> 
                            {/* </form> */}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No companies found</p>
            )}
        </div>
    );
};

export default AllUserCompanies;
