import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router";
import "./childComponents.css";
import image from "./../../images/profile_pic.png";
import PostModal from "./PostModal";

const FORM_STAGES = {
    VIEWING: "viewing",
    COMMENTING: "commenting",
    SUBMITTING: "submitting"
}

const UserHome = ({createPost,fetchPosts,storage,fetchUser,fetchJobs,createComment}) => {
    const [showModal,setModal] = useState(false);
    const [postContent,setPostContent] = useState("");
    const [posts,setPosts] = useState([]);
    const [fullName,setFullName] = useState("");
    const [currUser,setCurrUser] = useState('');
    const [selected,setSelected] = useState(false);
    const [commentDetails,setCommentDetails] = useState('');
    const [comments,setComments] = useState([]);
    const [currentStage,setCurrentStage] = (FORM_STAGES.VIEWING);
    // const [firstName,setFirstName] = useState('');
    // const [lastName,setLastName] = useState('');
    const navigate = useNavigate();

    const openModal = () => {setModal(true)};
    const closeModal = () => {setModal(false)};

    useEffect(()=> {
        getAllPosts();
    },[]);

    useEffect(() => {
        getCurrUser();
    },[])


    const getAllPosts = async() => {

        const postsData = await fetchPosts();
        setPosts(postsData);
    }

    const getUser = async(userId) => {
        const response = await fetchUser(userId);
        console.log(response);
        storage.setItem("otherUser",response.user_id);
        if (response.first_name){
          navigate(`/signed-in/feed/user/${userId}`);
        }
      }

    const getCurrUser = async(userId) => {
        const response = await fetchUser(storage.getItem("currUserId"));
        setCurrUser(response);
        storage.setItem("currUserProfile",response);
        console.log(response);
      }

    const newComment = async(postId) => {
        setCurrentStage(FORM_STAGES.COMMENTING);
        storage.setItem("commentPost",postId);
    }

    const handleCommentOption = async(postId) => {

        setCurrentStage(FORM_STAGES.SUBMITTING);

        const comment = {
            post_id: postId,
            content: commentDetails,
        }

        const newComment = createComment(comment);

        setComments((prevComments) => [...prevComments,newComment]);
    }

    const handleCreatePost = async(e) => {
        e.preventDefault();
        if (!postContent.trim()){
            alert("Post can't be empty");
            return;
        }

        const postData = {
            content: postContent.trim(),
        }
        const response = await createPost(postData);
        if (response){
            closeModal();
        }
        console.log("Here's what the backend response says: ",response);

    }

    const handleSearchUser = (e) => {
        e.preventDefault();
        // setFullName(value);
        // if (!fullName.trim()){
        //     alert("Search can't be empty");
        //     return;
        // }
        const name = fullName ?? "";
        const trimmed = name?.trim();
        console.log(trimmed);

        const spaceIdx = trimmed.indexOf(" ");
        console.log("idx: ",spaceIdx);
        if (spaceIdx !== -1){
            const firstName = (trimmed.slice(0,spaceIdx));
            const lastName =(trimmed.slice(spaceIdx+1));
            console.log("firstname: ",firstName);
            navigate(`/signed-in/feed/search-results?first_name=${encodeURIComponent(firstName)}&last_name=${encodeURIComponent(lastName)}`);
        }
        else{
            const firstName = trimmed;
            const lastName = "";
            // setLastName("");
            navigate(`/signed-in/feed/search-results?first_name=${encodeURIComponent(firstName)}&last_name=${lastName}`);

        }

    }

    const handleJobButton = (e) => {
        navigate("/signed-in/feed/suggested-jobs");
    }

    const handleSelected = (e) => {
        const value = e.target.value;
        setSelected(true);
        if (value === "create"){
            navigate("/signed-in/register/company");
        }
        if (value === "view"){
            navigate("/signed-in/feed/user-companies");
        }
    }


    const handleCreateOption = (e) => {
        navigate("/signed-in/feed/search-results")
    }

    const handleSignOut = (e) => {
        e.preventDefault();
        storage.removeItem("authToken");
        navigate("/");
    }

    return (
        <>
            <div className="home-page-container">
                {/* Left Sidebar */}
                <div className="home-left-sidebar">
                    <div className="user-profile-summary">
                        <img
                            className="user-profile-image-home-screen"
                            src={image}
                            alt="User Profile"
                        />
                        <h1 onClick={(e) => navigate("/signed-in/feed/user-profile/")}>
                            {currUser.first_name} {currUser.last_name}
                        </h1>
                    </div>

                    <div className="home-business-options">
                        <label htmlFor="options" className="user-home-company-drop-label">
                            For Businesses
                        </label>
                        <select id="options" value={selected} onChange={handleSelected}>
                            <option value="">--Select--</option>
                            <option value="create" onClick={handleCreateOption}>
                                Create Company
                            </option>
                            <option value="view">View Company</option>
                        </select>
                    </div>
                </div>

                {/* Main Content Column (Feed) */}
                <div className="home-main-content">
                    <div className="post-composer-container">
                        <input
                            onClick={openModal}
                            className="text-user-box-post"
                            type="text"
                            placeholder="Share a post..."
                        />
                        <div className="post-composer-buttons">
                            <form onSubmit={handleJobButton}>
                                <button className="job-button-home-screen-post" type="submit">
                                    üíº Jobs
                                </button>
                            </form>
                            <button
                                className="connections-button-home-screen-post"
                                onClick={(e) => navigate("/signed-in/feed/connections")}
                            >
                                üë®‚Äçüë©‚Äçüë¶ My Network
                            </button>
                        </div>
                    </div>

                    {/* Posts Feed */}
                    <ul className="post-list-container">
                        {posts.map((post) => (
                            <li key={post.id} className="post-content-list-home">
                                <div className="post-content-home">
                                    <div className="post-author-info">
                                        <button
                                            className="post-button-author-home"
                                            onClick={(e) => getUser(post.author_id)}
                                        >
                                            <span className="post-text-author-home">
                                                {post.author_first_name} {post.author_last_name}
                                            </span>
                                        </button>
                                    </div>
                                    <p className="post-content-body">{post.content}</p>
                                    <div className="post-actions">
                                        <button className="like-post">üëç</button>
                                        <button className="comment-post" type="button">
                                            üí¨
                                        </button>
                                    </div>

                                    {/* Commenting Section */}
                                    {currentStage === FORM_STAGES.COMMENTING && (
                                        <div className="comment-section">
                                            <textarea
                                                placeholder="Type a comment..."
                                                value={commentDetails}
                                                onChange={(e) => setCommentDetails(e.target.value)}
                                            />
                                            <button
                                                className="commenting-on-post"
                                                onClick={(e) => handleCommentOption(post.id)}
                                            >
                                                Comment
                                            </button>
                                        </div>
                                    )}
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>

                {/* Post Modal */}
                <PostModal isOpen={showModal} onClose={closeModal}>
                    <h2>What's on your mind?</h2>
                    <textarea
                        type="text"
                        placeholder="What do you want to post?"
                        rows="5"
                        value={postContent}
                        onChange={(e) => setPostContent(e.target.value)}
                        className="modal-textarea"
                    />
                    <div className="modal-actions">
                        <button onClick={closeModal}>Cancel</button>
                        <form onSubmit={handleCreatePost}>
                            <button type="submit">Share Post</button>
                        </form>
                    </div>
                </PostModal>

                {/* Global Search and Sign Out */}
                <form onSubmit={handleSignOut} className="sign-out-form">
                    <button type="submit" className="sign-out-button">
                        Sign Out
                    </button>
                </form>
                <form onSubmit={handleSearchUser} className="search-user-form">
                    <input
                        className="home-user-search-button"
                        type="text"
                        placeholder="Search üîç"
                        value={fullName}
                        onChange={(e) => setFullName(e.target.value)}
                    />
                </form>
            </div>
        </>
    );
}

export default UserHome;
