import React,{useState} from "react";
import { useNavigate } from "react-router";
import "./childComponents.css"

const FORM_STAGES = {
    FULL_NAME:"fullname",
    INDUSTRY:"industry",
    LOCATION:"location"
}

const CompanyProfileRegistration = ({createProfile}) => {
    const [fullName,setFullName] = useState('');
    const [website,setWebsite] = useState('');
    const [selected,setSelected] = useState('');
    const [bio,setBio] = useState('');
    const [country,setCountry] = useState('');
    const [city,setCity] = useState('');
    const [state,setState] = useState('');
    const [currentStage,setCurrentStage] = useState(FORM_STAGES.FULL_NAME);

    const navigate = useNavigate();

    const handleNameNextButton = async(e) => {
        e.preventDefault();
        if (!fullName.trim()){
            alert("Last Name is Required");
            return;
        }
        setCurrentStage(FORM_STAGES.INDUSTRY);
    }

    const handleBioNextButton = async(e) => {
        e.preventDefault();
        setCurrentStage(FORM_STAGES.LOCATION);
    }
    const handleCreateButton = async(e) => {
        e.preventDefault();
        const profile_data = {
            // first_name: firstName.trim(),
            last_name: fullName.trim(),
            bio: bio.trim(),
            location_city: city.trim(),
            location_country: country.trim(),
            location_state: state.trim(),
        }
        const response = await createProfile(profile_data);
        console.log(response);
        navigate("/signed-in/feed/");
    }

    return (
        <>
        <div>
        {currentStage=== FORM_STAGES.FULL_NAME && (<form onSubmit={handleNameNextButton}>
        <div>
        </div>
        <div>

        <input
        className="name-registration-box"
        type="text"
        placeholder="Enter Company Title"
        value={fullName}
        onChange={(e)=> setFullName(e.target.value)}
        />

        <input
        className="company-website-box"
        type="text"
        placeholder="Enter Company Website"
        value={website}
        onChange={(e) => setWebsite(e.target.value)}
        />
        </div>
        <div>
        <button className="next-button-">Next</button>
        </div>
        </form>
        )}
        {currentStage=== FORM_STAGES.INDUSTRY && (<form onSubmit={handleBioNextButton}>
        <div>
        <div className="user-home-company-drop-down">
        <label htmlFor="options" className="user-home-company-drop-label">For Businesses</label>
        <select id="options" value={selected} onChange={handleSelected}>
        <option value="">Select</option>
        <option value="create">Create Company</option>
        <option value="view">View Company</option>
        </select>
        </div>


        <button>Upload Company Logo</button>
        </div>
        {/* <div>
        <input
        className="last-name-registration-box"
        type="text"
        placeholder="Enter Last Name"
        value={lastName}
        onChange={(e)=> setLastName(e.target.value)}
        />
        </div> */}
        <div>
        <button className="next-bio-button">Next</button>
        </div>
        </form>
        )}
        {currentStage=== FORM_STAGES.LOCATION && (<form onSubmit={handleCreateButton}>
        <div className="location-register-container">
        <input
        className="location-country-registration-box"
        type="text"
        placeholder="Enter Country of Residence"
        value={country}
        onChange={(e) => setCountry(e.target.value)}
        />
        </div>
        <div>
        <input
        className="location-city-registration-box"
        type="text"
        placeholder="Enter City of Residence"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        />
        </div>
        <div>

        <input
        className="location-state-registration-box"
        type="text"
        placeholder="Enter State of Residence"
        value={state}
        onChange={(e) => setState(e.target.value)}
        />
        </div>
        {/* <div>
        <input
        className="last-name-registration-box"
        type="text"
        placeholder="Enter Last Name"
        value={lastName}
        onChange={(e)=> setLastName(e.target.value)}
        />
        </div> */}
        <div>
        <button className="create-profile-button">Create Profile</button>
        </div>
        </form>
        )}
        </div>
        </>
    )
}

export default CompanyProfileRegistration;
