import React,{useState} from "react";
import { useNavigate } from "react-router";

const FORM_STAGES = {
    FIRST_PAGE:"firstpage",
    SECOND_PAGE:"secondpage",
    THIRDPAGE:"thirdpage",
    FOURTHPAGE:"fourthpage",
    FIFTHPAGE: "fifthpage",
    SIXTHPAGE: "sixthpage",
    VIEW_QUESTIONS:"viewquestions",
    VIEW_QUESTIONS_TWO:"viewquestionstwo",
    VIEW_QUESTIONS_THREE:"viewquestionsthree"

}

const PostNewJob = ({createJob,fetchJob,storage}) => {
    const [title,setTitle] = useState('');
    const [website,setWebsite] = useState('');
    const [selected,setSelected] = useState('');
    const [selectedIndustry,setSelectedIndustry] = useState('');
    const [selectedSkillOne,setSelectedSkillOne] = useState('');
    const [selectedSkillTwo,setSelectedSkillTwo] = useState('');
    const [selectedSkillThree,setSelectedSkillThree] = useState('');
    const [selectedSkillFour,setSelectedSkillFour] = useState('');
    const [selectedSkillFive,setSelectedSkillFive] = useState('');
    const [selectedSkillS,setSelectedSkillS] = useState([]);
    const [description,setDescription] = useState('');
    const [country,setCountry] = useState('');
    const [city,setCity] = useState('');
    const [state,setState] = useState('');
    const [currentStage,setCurrentStage] = useState(FORM_STAGES.FIRST_PAGE);

    const [questions, setQuestions] = useState([
        {
            question: "Why do you believe you're a good canididate for this job?",
            answers: [],
            selectedType: 'Written Response'
        },
        {
            question: "Are you legally authorized to work in the United State?",
            answers: ["Yes","No"],
            selectedType: 'Multiple-choice'
        },
        {
            question: "How many years of experience do you have in this field?",
            answers: [],
            selectedType: 'Number Response'
        },
        {
            question: "What motivated you to apply for this role?",
            answers: [],
            selectedType: 'Written Response'
        },
        {
            question: "Which programming languages are you fluent in?",
            answers: ["Python","Java","C","C++","JavaScript"],
            selectedType: 'Checkbox'
        },
        {
            question: "Which Backend Frameworks do you have experience in?",
            answers: ["Django","FastAPI","Spring","Flask","Express.js","Nest.js","Actix Web","Rocket"],
            selectedType: 'Checkbox'
        },
        {
            question: "Please link your GitHub Page or a portfolio that showcases your work",
            answers: [],
            selectedType: 'Written Response'
        },
        {
            question: "Do you have any teaching experience in computer science? If so, please explain more",
            answers: [],
            selectedType: 'Written Response'
        },
        {
            question: "Are you open to learning new frameworks or programming languages?",
            answers: ["Yes","No"],
            selectedType: 'Multiple-choice'
        },
        {
            question: "What is your operating system of choice? ",
            answers: ["Mac OS","Windows","Linux","Raspberry Pi"],
            selectedType: 'Multiple-choice'
        },
        {
            question: "What is your availibility?",
            answers: [],
            selectedType: 'Availibility'
        },
        {
            question: "How many hours can you work a week?",
            answers: [],
            selectedType: 'Number Response'
        },
        {
            question: "Can you provide valid I-9 Documents if hired?",
            answers: ["Yes","No"],
            selectedType: 'Multiple-choice'
        }
    ]);

    const [jobQuestions,setJobQuestions] = useState([]);
    const [jobQuestionTypes,setJobQuestionTypes] = useState([]);
    const [jobQuestionAnswers,setJobQuestionAnswers] = useState([]);

    const navigate = useNavigate();

    // Helper function to update a single question in the array
    const updateQuestion = (index, key, value) => {
      setQuestions(prevQuestions =>
        prevQuestions.map((q, i) =>
          i === index ? { ...q, [key]: value } : q
        )
      );
    };

    // Helper function to update a multiple-choice option
    const updateOption = (questionIndex, optionIndex, value) => {
        setQuestions(prevQuestions => 
            prevQuestions.map((q, i) => {
                if (i === questionIndex) {
                    const newAnswers = [...q.answers];
                    newAnswers[optionIndex] = value;
                    return { ...q, answers: newAnswers };
                }
                return q;
            })
        );
    };

    // A new function to add a new question
    const handleAddQuestion = () => {
        setQuestions(prevQuestions => [
            ...prevQuestions,
            { question: "New Question", answers: [], selectedType: 'Written Response' }
        ]);
    };

    // A new function to add an option to a specific question
    const handleAddOption = (questionIndex) => {
        setQuestions(prevQuestions => 
            prevQuestions.map((q, i) =>
                i === questionIndex ? { ...q, answers: [...q.answers, ''] } : q
            )
        );
    };

    // A new function to delete a question by index
    const handleDeleteQuestion = (indexToDelete) => {
        if (questions.length <= 1) {
            alert("You must have at least one question.");
            return;
        }
        setQuestions(prevQuestions => 
            prevQuestions.filter((_, index) => index !== indexToDelete)
        );
    };

    // A new function to delete an option from a specific question
    const handleDeleteOption = (questionIndex, optionIndexToDelete) => {
        setQuestions(prevQuestions => 
            prevQuestions.map((q, i) => {
                if (i === questionIndex) {
                    const newAnswers = q.answers.filter((_, oIndex) => oIndex !== optionIndexToDelete);
                    return { ...q, answers: newAnswers };
                }
                return q;
            })
        );
    };

    const handleNextButton = async(e) => {
        e.preventDefault();
        if (!title.trim()){
            alert("Job Title is Required");
            return;
        }
        setCurrentStage(FORM_STAGES.SECOND_PAGE);
    }

    const handleNextButtonTwo = async(e) => {
        e.preventDefault();
        if (selectedSkillOne.trim() && selectedSkillTwo.trim() && selectedSkillThree.trim()){
            setSelectedSkillS((prevSkills) => [selectedSkillOne,selectedSkillTwo,selectedSkillThree]);
            console.log(selectedSkillOne);
        }
        else {
            alert("Select at least the first 3 skills")
            return;
        }
        if (selectedSkillFour.trim()){
            setSelectedSkillS((prevSkills) => [...prevSkills,selectedSkillFour])
        }
        if (selectedSkillFive.trim()){
            setSelectedSkillS((prevSkills) => [...prevSkills,selectedSkillFive])
        }
        setCurrentStage(FORM_STAGES.THIRDPAGE);
    }

    const handleNextButtonThree = async(e) => {
        e.preventDefault();
        setCurrentStage(FORM_STAGES.FOURTHPAGE);
    }

    const handleSelected = (e) => {
        const value = e.target.value;
        setSelected(value);
    }

    const handleSelectedSkillOne = (e) => {
        const value = e.target.value;
        setSelectedSkillOne(value);
    }

    const handleSelectedSkillTwo = (e) => {
        const value = e.target.value;
        setSelectedSkillTwo(value);
    }

    const handleSelectedSkillThree = (e) => {
        const value = e.target.value;
        setSelectedSkillThree(value);
    }

    const handleSelectedSkillFour = (e) => {
        const value = e.target.value;
        setSelectedSkillFour(value);
    }

    const handleSelectedSkillFive = (e) => {
        const value = e.target.value;
        setSelectedSkillFive(value);
    }

    const handleSelectedTwo = (e) => {
        e.preventDefault();
        const value = e.target.value;
        setSelectedIndustry(value);
    }

    const handleBackButtonQuestions = (e) => {
        e.preventDefault();
        setCurrentStage(FORM_STAGES.FOURTHPAGE);
    }

    const handleCreateButton = async(e) => {
        e.preventDefault();
        if (selected==="" || selectedIndustry===""){
            alert("Please select a Job Type and a Job Industry");
            return;
        }   

        const finalQuestions = questions.map(q => q.question);
        const finalQuestionTypes = questions.map(q => q.selectedType);
        const finalQuestionAnswers = questions.map(q => q.answers);

        setJobQuestions(finalQuestions);
        setJobQuestionTypes(finalQuestionTypes);
        setJobQuestionAnswers(finalQuestionAnswers);

        const profile_data = {
            company_id: storage.getItem("companyId"),
            title: title.trim(),
            description: description.trim(),
            link_to_job: website.trim(),
            industry: selectedIndustry,
            job_type: selected,
            location_city: city,
            location_country: country,
            location_state: state,
            skills: selectedSkillS,
            questions: finalQuestions,
            question_types: finalQuestionTypes,
            Youtubes: finalQuestionAnswers,
        }

        const response = await createJob(profile_data);
        if (response.title){
            storage.setItem("job",response.title);
            const fetchDetails = await fetchJob(response.name);
            navigate("/signedn/");
        }
    }

    return (
        <>
        <div>
        {currentStage=== FORM_STAGES.FIRST_PAGE && (<form onSubmit={handleNextButton}>
        <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
        <div>
        </div>
        <div>
        <label className="name-registration-label">Job Title</label>
        <input
        className="name-registration-box"
        type="text"
        placeholder="Enter Job Title"
        value={title}
        onChange={(e)=> setTitle(e.target.value)}
        />
        <label className="website-registration-label">Job Website</label>
        <input
        className="company-website-box"
        type="text"
        placeholder="Enter Job Website"
        value={website}
        onChange={(e) => setWebsite(e.target.value)}
        />
        <div className="user-home-industry-drop-down">
        <label htmlFor="options" className="user-home-industry-drop-label">Job Type: </label>
        <select id="options" value={selected} onChange={(e) => handleSelected(e)}>
        <option value="" disabled>--Select--</option>
        <option value="on-site">On-site</option>
        <option value="hybrid">Hybrid</option>
        <option value="remote">Remote</option>
        </select>
        </div>
        </div>
        <div>
        <button className="next-button-">Next</button>
        </div>
        </form>
        )}
        {currentStage === FORM_STAGES.SECOND_PAGE && (<form onSubmit={handleNextButtonTwo}>
            <div>
            <button onClick={(e) => setCurrentStage(FORM_STAGES.FIRST_PAGE)} className="back-button">Back</button>

            <div className="user-home-industry-drop-down">
                <label htmlFor="options" className="user-home-industry-drop-label">Skills: </label>
                <select id="options" value={selectedSkillOne} onChange={(e) => handleSelectedSkillOne(e)}>
                <option value="" disabled>--Required--</option>
                <option value="Public Speaking">Public Speaking</option>
                <option value="Communication">Communication</option>
                <option value="Leadership">Leadership</option>
                </select>
                <div>
                <select id="options" value={selectedSkillTwo} onChange={(e) => handleSelectedSkillTwo(e)}>
                <option value="" disabled>--Required--</option>
                <option value="Teamwork">Teamwork</option>
                <option value="Problem Solving">Problem Solving</option>
                <option value="Fast Learner">Fast Learner</option>
                </select>
                </div>
                <div>
                <select id="options" value={selectedSkillThree} onChange={(e) => handleSelectedSkillThree(e)}>
                <option value="" disabled>--Required--</option>
                <option value="Customer Service">Customer Service</option>
                <option value="Attention to Detai">Attention to Detail</option>
                <option value="Initiative">Initiative</option>
                </select>
                </div>
                <div>
                <select id="options" value={selectedSkillFour} onChange={(e) => handleSelectedSkillFour(e)}>
                <option value="" disabled>--Optional--</option>
                <option value="Python">Python</option>
                <option value="Java">Java</option>
                <option value="C">C</option>
                <option value="C++">C++</option>
                </select>
                </div>
                <div>
                <select id="options" value={selectedSkillFive} onChange={(e) => handleSelectedSkillFive(e)}>
                <option value="" disabled>--Optional--</option>
                <option value="SQL">SQL</option>
                <option value="MongoDB">MongoDB</option>
                <option value="Firebase">Firebase</option>
                <option value="DynamoDB">DynamoDB</option>
                </select>
                </div>
            </div>
            <button className="next-button-">Next</button>
            </div>
        </form>)}
        {currentStage=== FORM_STAGES.THIRDPAGE && (<form onSubmit={handleNextButtonThree}>
        <div>
        <button onClick={(e) => setCurrentStage(FORM_STAGES.SECOND_PAGE)} className="back-button">Back</button>
        <textarea
        className="bio-registration-box"
        type="text"
        placeholder="Enter Job Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        />
        </div>
        <div>
        <button className="next-bio-button">Next</button>
        </div>
        </form>
        )}

        {currentStage=== FORM_STAGES.FOURTHPAGE && (<form onSubmit={handleCreateButton}>
            <div>
            <button onClick={(e) => setCurrentStage(FORM_STAGES.THIRDPAGE)} className="back-button">Back</button>
            <div className="user-home-industry-drop-down">
            <button type="button" onClick={() => setCurrentStage(FORM_STAGES.VIEW_QUESTIONS)}>Pre-Set Job Questions</button>            

            <label htmlFor="options" className="user-home-industry-drop-label">Industry: </label>
            <select id="options" value={selectedIndustry} onChange={(e) => handleSelectedTwo(e)}>
            <option value="" disabled>--Select--</option>
            <option value="Technology">Technology</option>
            <option value="Finance">Finance</option>
            <option value="Healthcare">Healthcare</option>
            <option value="Education">Education</option>
            <option value="Retail">Retail</option>
            </select>
            </div>
            <button>Upload Company Logo</button>
            <div>
            <button className="create-profile-button">Create Job</button>
            </div>
            </div>
            </form>
        )}
        {/* Dynamic question rendering for all view stages */}
        { (currentStage === FORM_STAGES.VIEW_QUESTIONS || 
           currentStage === FORM_STAGES.VIEW_QUESTIONS_TWO || 
           currentStage === FORM_STAGES.VIEW_QUESTIONS_THREE) && (
            <form onSubmit={handleBackButtonQuestions}>
                <div>
                    {questions.map((q, index) => (
                        <div key={index} style={{ marginBottom: '1rem' }}>
                            <input
                                type="text"
                                placeholder={`Question ${index + 1}`}
                                value={q.question}
                                onChange={(e) => updateQuestion(index, 'question', e.target.value)}
                            />
                            <select 
                                value={q.selectedType} 
                                onChange={(e) => updateQuestion(index, 'selectedType', e.target.value)}
                            >
                                <option value="Written Response">Open Response</option>
                                <option value="Multiple-choice">Multiple Choice</option>
                                <option value="File Upload">File Upload</option>
                                <option value="Number Response">Number Response</option>
                                <option value="Availibility">Availibility</option>
                                <option value="Checkbox">Checkbox</option>
                            </select>
                            <button type="button" onClick={() => handleDeleteQuestion(index)}>Delete</button>

                            {(q.selectedType === 'Multiple-choice' || q.selectedType === 'Checkbox') && (
                                <div style={{ marginLeft: '20px', marginTop: '10px' }}>
                                    {q.answers.map((answer, optionIndex) => (
                                        <div key={optionIndex}>
                                            <input
                                                type="text"
                                                placeholder={`Option ${optionIndex + 1}`}
                                                value={answer}
                                                onChange={(e) => updateOption(index, optionIndex, e.target.value)}
                                            />
                                            <button 
                                                type="button" 
                                                onClick={() => handleDeleteOption(index, optionIndex)}
                                            >
                                                Delete Option
                                            </button>
                                        </div>
                                    ))}
                                    <button type="button" onClick={() => handleAddOption(index)}>
                                        Add Option
                                    </button>
                                </div>
                            )}
                        </div>
                    ))}
                    <div>
                        <button type="button" onClick={handleAddQuestion}>Add New Question</button>
                        <button type="submit">Back to Job App</button>
                    </div>
                </div>
            </form>
        )}

        {currentStage === FORM_STAGES.SIXTHPAGE && (
        <button className="create-profile-button">Create Job</button>
        )}
        </div>
        </>
    )
}

export default PostNewJob;
