import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router";
import PostModal from "./PostModal";
import "./childComponents.css";
import image from "./../../images/profile_pic.png";

const MyUserProfile = ({storage,fetchUser,fetchAllSkills,fetchUserSkills,addUserSkills,addUserWorkExperience}) => {
const [currUser,setCurrUser] = useState('');
const [allSkills,setAllSkills] = useState([]);
const [allWorkExperience,setAllWorkExperience] = useState([]);
const [fetchedSkills,setFetchedSkills] = useState([]);
const [mySkills,setMySkills] = useState([]);
const [workTitle,setWorkTitle] = useState('');
const [workDescription,setWorkDescription] = useState('');
const [companyName,setCompanyName] = useState('');
const [workSkills,setWorkSkills] = useState([]);
const [workType,setWorkType] = useState('');
const [locationState,setLocationState] = useState('')
const [locationCountry,setLocationCountry] = useState('')
const [showModal,setShowModal] = useState(false);
const navigate = useNavigate();
console.log(currUser.first_name);

const isOpen = () =>  setShowModal(true);
const isClosed = () => setShowModal(false);

useEffect(() => {

    getCurrUser();
    getSkills();
},[])

const getCurrUser = async() => {
    const response = await fetchUser(storage.getItem("currUserId"));
    setCurrUser(response);
    storage.setItem("currUserProfile",response);
    console.log(response);
  }

const getSkills = async() => {
    const response = await fetchAllSkills();
    setAllSkills(response);
    const response_two = await fetchUserSkills();
    setFetchedSkills(response_two);

}


const handleAddSkills = async(skill) => {
    setMySkills((prevSkills) => [...prevSkills,skill]);
    const updatedSkills = allSkills.filter(
        (skill_item) => skill_item.id !== skill.id
    )
    setAllSkills(updatedSkills);
    console.log(mySkills);

}

const handleSubmitNewSkills = async(e) => {
    e.preventDefault();
    const response = addUserSkills(mySkills);
    if (response){
        setFetchedSkills(prevSkills => [...prevSkills,...mySkills]);
        isClosed();
    }

}

const handleSubmitNewWorkExperience = async(e) => {
    e.preventDefault();

    const fullJobDetails = {

    }

    const response = addUserWorkExperience();
    if (response){
        setFetchedSkills(prevSkills => [...prevSkills,...mySkills]);
        isClosed();
    }

}

return (
    <>
    <div>
    <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
    <div className="user-profile-name-profile-pic-container">
    <img className="user-profile-image-screen" src={image}></img>
    <h2>{currUser.first_name} {currUser.last_name}</h2>
    {currUser.location_country && currUser.location_state && (
    <h2 className="user-profile-location-screen">{currUser.location_state}, {currUser.location_country}</h2>
    )}
    {currUser.location_country && !currUser.location_state && (
    <h2 className="user-profile-location-screen">{currUser.location_country}</h2>
    )}
    {!currUser.location_country && !currUser.location_state && (
    <button className="user-profile-location-screen">Add Location</button>
    )}

    </div>
    <div>
    <p>
    <h1>Work Experience</h1>
    <button onClick={isOpen}>➕ Add</button>
    {allWorkExperience.map((WorkExperience) => (
        <div>
        <p>
        {/* <button className="my-skills-my-profile-no-modal-button">{WorkExperience.company_name}</button> */}
        <h4>{WorkExperience.company_name}</h4>
        <h5>{WorkExperience.title} :</h5>
        <h5>{WorkExperience.description}</h5>
        </p>
        </div>
    ))}
    </p>
    <PostModal isOpen={showModal} onClose={isClosed}>
    <h1>Add Work Experience</h1>
    </PostModal>
    </div>
    <p>
    <h1>Skills</h1>
    <button onClick={isOpen}>➕ Add</button>
    <p className="my-skills-my-profile-no-modal-container-paragraph">
    {fetchedSkills.map((currentSkill) => (
        <div>
        <button className="my-skills-my-profile-no-modal-button">{currentSkill.skill_name}</button>
        </div>
    ))}
    </p>
    <PostModal isOpen={showModal} onClose={isClosed}>
    {mySkills.length === 0 && (
        <div>
        <h2>My Skills:</h2>
        <p className="skills-my-profile-modal">
        {fetchedSkills.map((currentSkills) => (
            <div>
            <button className="my-skills-my-profile-button">- {currentSkills.skill_name}</button>
            </div>
        ))}
        </p>
        <p className="skills-my-profile-modal">
        {mySkills.map((skill) => (
            <div>
            <button className="my-skills-my-profile-button">- {skill.skill_name}</button>
            </div>
        ))}
        </p>
        <h1>Skills</h1>
        <p className="skills-my-profile-modal">
        {/* <button>➕Communication</button> */}
        {allSkills.map((skill) => (
            <div>
            <button className="skills-my-profile-button" onClick={(e) => (handleAddSkills(skill))}>+ {skill.skill_name}</button>
            </div>
        ))}
        </p>
        </div>
    )}
        {mySkills.length > 0 && (
        <div>

        <h2>My Skills:</h2>
        <p className="skills-my-profile-modal">
        {fetchedSkills.map((currentSkills) => (
            <div>
            <button className="my-skills-my-profile-button">- {currentSkills.skill_name}</button>
            </div>
        ))}
        </p>
        <p className="skills-my-profile-modal">
        {mySkills.map((skill) => (
            <div>
            <button className="added-skills-my-profile-button">- {skill.skill_name}</button>
            </div>
        ))}
        </p>
        <h1>Skills</h1>
        <p className="skills-my-profile-modal">
        {/* <button>➕Communication</button> */}
        {allSkills.map((skill) => (
            <div>
            <button className="skills-my-profile-button" onClick={(e) => (handleAddSkills(skill))}>+ {skill.skill_name}</button>
            </div>
        ))}
        </p>
        <form onSubmit={handleSubmitNewSkills}>
        <button type="submit">Submit</button>
        </form>
        </div>
    )}
    </PostModal>
    </p>
    </div>
    </>
)


}

export default MyUserProfile;
