import React,{useState} from "react";
import { useNavigate } from "react-router";
import "./childComponents.css"

const FORM_STAGES = {
    FULL_NAME:"fullname",
    BIO:"bio",
    LOCATION:"location"
}

const UserProfileRegistration = (createProfile) => {
    const [firstName,setFirstName] = useState('');
    const [lastName,setLastName] = useState('');
    const [bio,setBio] = useState('');
    const [currentStage,setCurrentStage] = useState(FORM_STAGES.FULL_NAME);

    const handleNameNextButton = async(e) => {
        e.preventDefault();
        if (!firstName.trim()){
            alert("First Name is Required");
            return;
        }
        if (!lastName.trim()){
            alert("Last Name is Required");
            return;
        }
        setCurrentStage(FORM_STAGES.BIO);
    }

    return (
        <>
        <div>
        {currentStage=== FORM_STAGES.FULL_NAME && (<form onSubmit={handleNameNextButton}>
        <div>
        <input
        className="first-name-registration-box"
        type="text"
        placeholder="Enter First Name"
        value={firstName}
        onChange={(e) => setFirstName(e.target.value)}
        />
        </div>
        <div>
        <input
        className="last-name-registration-box"
        type="text"
        placeholder="Enter Last Name"
        value={lastName}
        onChange={(e)=> setLastName(e.target.value)}
        />
        </div>
        <div>
        <button className="next-button-">Next</button>
        </div>
        </form>
        )}
        {currentStage=== FORM_STAGES.BIO && (<form onSubmit={handleNameNextButton}>
        <div>
        <textarea
        className="bio-registration-box"
        type="text"
        placeholder="Enter Bio"
        value={bio}
        onChange={(e) => setBio(e.target.value)}
        />
        </div>
        {/* <div>
        <input
        className="last-name-registration-box"
        type="text"
        placeholder="Enter Last Name"
        value={lastName}
        onChange={(e)=> setLastName(e.target.value)}
        />
        </div> */}
        <div>
        <button className="next-bio-button">Next</button>
        </div>
        </form>
        )}
        {currentStage=== FORM_STAGES.LOCATION && (<form onSubmit={handleNameNextButton}>
        <div>
        <input
        className="location-cit-registration-box"
        type="text"
        placeholder="Enter Bio"
        value={bio}
        onChange={(e) => setBio(e.target.value)}
        />
        </div>
        {/* <div>
        <input
        className="last-name-registration-box"
        type="text"
        placeholder="Enter Last Name"
        value={lastName}
        onChange={(e)=> setLastName(e.target.value)}
        />
        </div> */}
        <div>
        <button className="next-bio-button">Next</button>
        </div>
        </form>
        )}
        </div>
        </>
    )
}

export default UserProfileRegistration;
