import React,{useState,useEffect} from "react";
import { useNavigate, useParams } from "react-router";
import image from "./../../images/profile_pic.png"
import "./childComponents.css";

const OtherCompanyProfilePage = ({getIndividualJob, getCompany,storage,getJobs}) => {
    const [otherCompany,setOtherCompany] = useState("");
    // const [otherUserSkills,setOtherUserSkills] = useState("");
    const [jobs,setJobs] = useState([]);
    const [connect,setConnect] = useState(false);
    const { otherUserId } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        loadCompany()
},[])
    useEffect(() => {
        loadCompanyJobs()
    },[])

        const loadCompany = async() => {
            const response = await getCompany((storage.getItem("otherCompany")));
            console.log(response.status)
            console.log(response)
            if (response.user){
                setOtherCompany(response);
                // setOtherUserSkills(response.skills);
            }
            else{
                console.log("Bad Response: ",response);
            }
            // storage.setItem("otherUser",response);
            // setOtherUser(storage.getItem("otherUser"));
            // console.log("Other user: ",otherUser);
        }

        const loadCompanyJobs = async() => {
            const response = await getJobs((storage.getItem("otherCompany")));
            console.log(response)
            if (response){
                setJobs(response);
            }
            else{
                console.log("Bad Response: ",response);
            }
            // storage.setItem("otherUser",response);
            // setOtherUser(storage.getItem("otherUser"));
            // console.log("Other user: ",otherUser);
        }

        const handleClickJob = async() => {
            // const userData = {
            //     user_id:otherUser.user_id,
            // }
            // console.log(storage.getItem("otherUser"));
            const response = await getIndividualJob(otherCompany);
            if (response === null){
                alert("This user is already your friend");
                return;
            }
            setConnect(true);
            if (response.id){
                alert("Friend Request sent");
                return
            }
            console.log("An error occured ",response);
        }

        return (
            <>
            <div>
                <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
                <div className="other-profile-name-profile-pic-container">
                <img className="other-profile-image-screen" src={image}></img>
                <h2>{otherCompany.company_name}</h2>
                {/* <h2 className="user-profile-location-screen">{otherUser.location_state}, {otherUser.location_country}</h2> */}
                </div>
                <h1 className="other-user-bio-header-profile">About:</h1>
                <h2 className="other-user-bio-profile">{otherCompany.description}</h2>

                {/* <h3>Skills</h3>
                {Array.isArray(otherUserSkills) && (
                <div>
                {otherUserSkills.map((skill)=>(
                    <div>
                    <button>{skill.skill_name}</button>
                    </div>
                ))}
                </div>
                )} */}
                <h3>Jobs:</h3>
                {jobs.map((job)=> (
                    <li key={job.id} className="post-content-list-home">
                        <p className="post-content-home">
                            <button className="post-button-author-home">
                            <text className="post-text-author-home" onClick={(e)=>getCompany(post.author_id)}>{job.title}</text>
                            </button>
                            <text className="post-content-body" >{job.description}</text>
                            {/* <text className="post-content-time" >{post.date_posted}</text> */}
                        </p>
                    </li>
                ))}
            </div>
            </>
        )

}

export default OtherCompanyProfilePage;
