import React,{useState} from 'react';
import { useNavigate } from 'react-router';

// const FORM_STAGES = {
//     EMAIL: "email",
//     PASSWORD: "password",
// }

const AccountRegister = ({createAccount}) => {
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [password2,setPassword2] = useState('');

    const navigate = useNavigate();

    const handlRegisterButton = (e) =>{
        e.preventDefault();
        if (!email.trim()){
            alert("Email can't be empty");
            return;
        }
        if (!password.trim()){
            alert("Password can't be empty");
            return;
        }
        if (!password2.trim()){
            alert("Password can't be empty");
            return;
        }
        if (password !== password2){
            alert("Passwords don't match");
            return;
        }

        const fullAccount = {
            email: email.trim(),
            password:password.trim(),
        }
        const response = createAccount(fullAccount);
        if (response){
            navigate("/register/await/");
            // navigate("/register/email_verification/");
        }
        else{
            alert("Email is already linked to existing account. Please use another email");
        }


    }
    return (
        <>
        <div>
        <form onSubmit={handlRegisterButton}>
        <div>
        <input
        className='email-input-register-box'
        type='text'
        placeholder='Enter Email'
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        />
        </div>
        <div>
        <input
        className='password-input-register-box'
        type='text'
        placeholder='Enter Password'
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        />
        </div>
        <div>
        <input
        className='password-input-register-box'
        type='text'
        placeholder='Re-Enter Password'
        value={password2}
        onChange={(e) => setPassword2(e.target.value)}
        />
        </div>
        <div>
        <button className='register-button' type='submit'>Register Account</button>
        </div>
        </form>
        </div>
        </>
    )
}

export default AccountRegister;
