import React,{useState} from "react";
import { useNavigate } from "react-router";
import "./childComponents.css"
import URLS from "./URLS";

const FORM_STAGES = {
    FULL_NAME:"fullname",
    INDUSTRY:"industry",
    BIO:"bio"
}

const CompanyProfileRegistration = ({createProfile,fetchProfile,storage}) => {
    const [fullName,setFullName] = useState('');
    const [website,setWebsite] = useState('');
    const [selected,setSelected] = useState('');
    const [selectedIndustry,setSelectedIndustry] = useState('');
    const [bio,setBio] = useState('');
    const [country,setCountry] = useState('');
    const [city,setCity] = useState('');
    const [state,setState] = useState('');
    const [currentStage,setCurrentStage] = useState(FORM_STAGES.FULL_NAME);

    const navigate = useNavigate();

    const handleNameNextButton = async(e) => {
        e.preventDefault();
        if (!fullName.trim()){
            alert("Company Name is Required");
            return;
        }
        if (!website.trim()){
            alert("Company Website is Required");
            return;
        }

        setCurrentStage(FORM_STAGES.INDUSTRY);
    }


    const handleBioNextButtonTwo = async(e) => {
        e.preventDefault();
        setCurrentStage(FORM_STAGES.BIO);
    }

    const handleSelected = (e) => {
        // e.preventDefault();
        const value = e.target.value;
        setSelected(value);
        // if (selected === ''){
        //     alert("The Company Type is required");
        //     return;
        // }
    }

    const handleSelectedTwo = (e) => {
        e.preventDefault();
        const value = e.target.value;
        setSelectedIndustry(value);
        // if (selectedIndustry === ''){
        //     alert("The Company Type is required");
        //     return;
        // }
    }

    const handleCreateButton = async(e) => {
        e.preventDefault();
        if (selected==="" || selectedIndustry===""){
            alert("Please select a Company Type and a Company Industry");
            return;
        }   
        const profile_data = {
            // first_name: firstName.trim(),
            name: fullName.trim(),
            description: bio.trim(),
            website: website.trim(),
            industry: selectedIndustry,
            company_type: selected,
            logo: URLS,
        }

        const response = await createProfile(profile_data);
        // console.log(response);
        if (response.company_id){
            // storage.setItem("companyName",response.name);
            storage.setItem("companyId",response.company_id);
            // const fetchDetails = await fetchProfile(response.name);
            // navigate("/signedn/");
            navigate(`/signed-in/feed/user-companies/${response.company_id}`);

        }
    }

    return (
        <>
        <div>
        {currentStage=== FORM_STAGES.FULL_NAME && (<form onSubmit={handleNameNextButton}>
        <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
        <div>
        </div>
        <div>
        {/* "Public Company","Self-Employed","Government Agency",
                           "Non-Profit","Sole Proprietorship","Privately Held","Partnership" */}

        <label className="name-registration-label">Company Name</label>
        <input
        className="name-registration-box"
        type="text"
        placeholder="Enter Company Title"
        value={fullName}
        onChange={(e)=> setFullName(e.target.value)}
        />
        <label className="website-registration-label">Company Website</label>
        <input
        className="company-website-box"
        type="text"
        placeholder="Enter Company Website"
        value={website}
        onChange={(e) => setWebsite(e.target.value)}
        />

        <div className="user-home-industry-drop-down">
        <label htmlFor="options" className="user-home-industry-drop-label">Type of Company</label>
        <select id="options" value={selectedIndustry} onChange={(e) => handleSelectedTwo(e)}>
        <option value="" disabled>--Select--</option>
        <option value="Technology">Technology</option>
        <option value="Finance">Finance</option>
        <option value="Healthcare">Healthcare</option>
        <option value="Education">Education</option>
        <option value="Retail">Retail</option>

        {/* ('Finance'),
('Healthcare'),
('Education'),
('Retail'),
('Manufacturing'),
('Transportation & Logistics'),
('Energy & Utilities'),
('Real Estate'),
('Government'),
('Legal'),
('Media & Entertainment'),
('Marketing & Advertising'),
('Hospitality & Travel'),
('Food & Beverage'),
('Construction'),
('Nonprofit & NGOs'),
('Agriculture'),
('Telecommunications'),
('Aerospace & Defense'),
('Insurance'),
('Biotechnology'),
('Human Resources'),
('Environmental Services'),
('Automotive'),
('Sports & Recreation'),
('Consumer Goods'),
('E-commerce'),
('Arts & Design'),
('Cybersecurity'); */}

        </select>
        </div>

        </div>
        <div>
        <button className="next-button-">Next</button>
        </div>
        </form>
        )}
        {currentStage=== FORM_STAGES.INDUSTRY && (<form onSubmit={handleBioNextButtonTwo}>
        <div>
        <button onClick={(e) => setCurrentStage(FORM_STAGES.FULL_NAME)} className="back-button">Back</button>
        <div className="user-home-industry-drop-down">
        <label htmlFor="options" className="user-home-industry-drop-label">Industry</label>
        <select id="options" value={selected} onChange={(e) => handleSelected(e)}>
        <option value="" disabled>--Select--</option>
        <option value="Public-Company">Public Company</option>
        <option value="Self-Employed">Self-Employed</option>
        <option value="Government-Agency">Government Agency</option>
        <option value="Non-Profit">Non-Profit</option>
        <option value="Sole-Proprietorship">Sole Proprietorship</option>
        <option value="Privately-Held">Privately Held</option>
        <option value="Partnership">Partnership</option>
        </select>
        </div>
        <label></label>


        <button>Upload Company Logo</button>
        <div>
        <button className="next-bio-button">Next</button>
        </div>
        </div>
        </form>
        )}
        {currentStage=== FORM_STAGES.BIO && (<form onSubmit={handleCreateButton}>
        <div>
        <button onClick={(e) => setCurrentStage(FORM_STAGES.INDUSTRY)} className="back-button">Back</button>
        <textarea
        className="bio-registration-box"
        type="text"
        placeholder="(Optional) Enter Bio"
        value={bio}
        onChange={(e) => setBio(e.target.value)}
        />
        </div>
        {/* <div>
        <input
        className="last-name-registration-box"
        type="text"
        placeholder="Enter Last Name"
        value={lastName}
        onChange={(e)=> setLastName(e.target.value)}
        />
        </div> */}
        <div>
        <button className="create-profile-button">Create Profile</button>
        </div>
        </form>
        )}
        </div>
        </>
    )
}

export default CompanyProfileRegistration;
