    const [connections,setConnections] = useState([]);
    const navigate = useNavigate('');

    useEffect(() => {
        loadConnections();
    },[])

    const loadConnections = async() => {
        const response = await fetchAllConnections();
        // if (response.length !== 0){
            setConnections(response);
        // }
    }

    const getUser = async(userId) => {
        const response = await fetchUser(userId);
        storage.setItem("otherUser",response.user_id);
        if (response.first_name){
          navigate(`/signed-in/feed/user/${userId}`);
        }
      }

    // const handleClickJob = async(connectionId) => {
    //     storage.setItem("connectionId",connectionId);
    //     navigate(`/signed-in/feed/job/${connectionId}`);
    // }
    return (
        <>
        <div>
        <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
        <h2 className="list-of-jobs-user-feed-home">All Your Connections</h2>
        {connections.map((connection)=>(
            <li key={connection.user_id} className="home-search-job-result-list"> 
            {connection.connection_data.status === "pending" && (
            <p>{connection.connection_data.status}ðŸŸ </p>
            )}
            <button className="home-search-job-result-list-button" onClick={(e) => getUser(connection.friend_profile_data.user_id)}>
            <h3 className="title-job-home-screen-text">Name:</h3>
            <h4>{connection.friend_profile_data.first_name} {connection.friend_profile_data.last_name}</h4>
            {/* <h3 className="title-job-home-screen-text">Job Title:</h3>
            <h4>{job.title}</h4> */}
            <h3 className="title-job-home-screen-text">Bio:</h3>
            <h4>{connection.friend_profile_data.bio}</h4>

            </button>
            </li>
        ))}

        </div>
        </>
    )

}

export default AllUserConnections;
