import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router";
import "./childComponents.css";

const AllRequestsUserProfile = ({fetchAllRequests,storage}) => {
    const [requests,setRequests] = useState([]);
    const navigate = useNavigate('');

    useEffect(() => {
        loadRequests();
    },[])

    const loadRequests = async() => {
        const response = await fetchAllRequests();
        // if (response.length !== 0){
            setRequests(response);
        // }
    }

    const handleClickUser = async(jobId) => {
        storage.setItem("jobId",jobId);
        navigate(`/signed-in/feed/user/${jobId}`);
    }
    return (
        <>
        <div>
        <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
        <h2 className="list-of-jobs-user-feed-home">All Connection Requests</h2>
        {requests.map((request)=>(
            <li key={request.user_id} className="home-search-job-result-list"> 
            <button className="home-search-job-result-list-button" onClick={(e) => handleClickUser(request.user_id)}>
            <h3 className="title-job-home-screen-text">Name:</h3>
            <h4>{request.first_name} {request.last_name}</h4>
            {/* Add Employee role, or position. */}
            </button>
            <div>
            <button></button>
            </div>
            </li>
        ))}

        </div>
        </>
    )

}

export default AllRequestsUserProfile;
