import exp from "constants";
import React,{useState} from "react";
import { useNavigate } from "react-router";
import { useSearchParams } from "react-router-dom";


const SearchPage = () => {
    const [searchQuery] = useSearchParams;
    const query = searchQuery.get("query") || "";
    const [results,setResults] = useState([]);

    useEffect(() => {
      const [firstName, ...rest] = query.trim().split(' ');
      const lastName = rest.join(' '); // handle middle names too

      if (firstName) {
        fetchUsersByName(firstName, lastName)
          .then(setResults)
          .catch((err) => {
            console.error(err);
            setError('Something went wrong while fetching.');
          });
      }
    }, [query]);

      return (
        <>
        <div>
            {results.map((result) => (
                <li key={result.user_id} className="home-search-result-list">
                <button className="home-search-result-list-button"><p>{result.first_name} {result.last_name}</p></button>
                </li>
            ))}
        </div>
        </>
      )
}

export default SearchPage;
