import React,{useState} from "react";
import { useNavigate } from "react-router";

const FORM_STAGES = {
    FIRST_PAGE:"firstpage",
    SECOND_PAGE:"secondpage",
    THIRDPAGE:"thirdpage",
    FOURTHPAGE:"fourthpage"
}

const PostNewJob = ({createJob,fetchJob,storage}) => {
    const [title,setTitle] = useState('');
    const [website,setWebsite] = useState('');
    const [selected,setSelected] = useState('');
    const [selectedIndustry,setSelectedIndustry] = useState('');
    const [selectedSkillOne,setSelectedSkillOne] = useState('');
    const [selectedSkillTwo,setSelectedSkillTwo] = useState('');
    const [selectedSkillThree,setSelectedSkillThree] = useState('');
    const [selectedSkillFour,setSelectedSkillFour] = useState('');
    const [selectedSkillFive,setSelectedSkillFive] = useState('');
    const [selectedSkillS,setSelectedSkillS] = useState([]);

    const [description,setDescription] = useState('');
    const [country,setCountry] = useState('');
    const [city,setCity] = useState('');
    const [state,setState] = useState('');
    const [currentStage,setCurrentStage] = useState(FORM_STAGES.FIRST_PAGE);

    const navigate = useNavigate();

    const handleNextButton = async(e) => {
        e.preventDefault();
        if (!title.trim()){
            alert("Job Name is Required");
            return;
        }
        if (!website.trim()){
            alert("Company Website is Required");
            return;
        }

        setCurrentStage(FORM_STAGES.SECOND_PAGE);
    }


    const handleNextButtonTwo = async(e) => {
        e.preventDefault();
        if (selectedSkillOne.trim() && selectedSkillTwo.trim() && selectedSkillThree.trim()){
            setSelectedSkillS((prevSkills) => [...prevSkills,selectedSkillOne,selectedSkillTwo,selectedSkillThree]);
        }
        else {
            alert("Select at least the first 3 skills")
            return;
        }
