import React,{useState} from "react";
import { useNavigate } from "react-router";
import "./childComponents.css"

const FORM_STAGES = {
    FULL_NAME:"fullname",
    INDUSTRY:"industry",
    LOCATION:"location"
}

const CompanyProfileRegistration = ({createProfile}) => {
    const [fullName,setFullName] = useState('');
    const [website,setWebsite] = useState('');
    const [selected,setSelected] = useState('');
    const [selectedIndustry,setSelectedIndustry] = useState('');
    const [bio,setBio] = useState('');
    const [country,setCountry] = useState('');
    const [city,setCity] = useState('');
    const [state,setState] = useState('');
    const [currentStage,setCurrentStage] = useState(FORM_STAGES.FULL_NAME);

    const navigate = useNavigate();

    const handleNameNextButton = async(e) => {
        e.preventDefault();
        if (!fullName.trim()){
            alert("Last Name is Required");
            return;
        }
        if (!website.trim()){
            alert("Company Website is Required");
            return;
        }

        setCurrentStage(FORM_STAGES.INDUSTRY);
    }

    const handleBioNextButton = async(e) => {
        e.preventDefault();
        setCurrentStage(FORM_STAGES.LOCATION);
    }

    const handleSelected = (e) => {
        e.preventDefault();
        const value = e.target.value;
        setSelected(value)
    }

    const handleSelectedTwo = (e) => {
        e.preventDefault();
        const value = e.target.value;
        setSelectedIndustry(value)
    }

    const handleCreateButton = async(e) => {
        e.preventDefault();
        const profile_data = {
            // first_name: firstName.trim(),
            company_name: fullName.trim(),
            website: website.trim(),
            industry_field: selectedIndustry,
            org_type: selected,
            location_country: country.trim(),
            location_state: state.trim(),
        }
        const response = await createProfile(profile_data);
        // console.log(response);
        navigate("/signed-in//");
    }

    return (
        <>
        <div>
        {currentStage=== FORM_STAGES.FULL_NAME && (<form onSubmit={handleNameNextButton}>
        <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
        <div>
        </div>
        <div>
        {/* "Public Company","Self-Employed","Government Agency",
                           "Non-Profit","Sole Proprietorship","Privately Held","Partnership" */}
        <input
        className="name-registration-box"
        type="text"
        placeholder="Enter Company Title"
        value={fullName}
        onChange={(e)=> setFullName(e.target.value)}
        />

        <input
        className="company-website-box"
        type="text"
        placeholder="Enter Company Website"
        value={website}
        onChange={(e) => setWebsite(e.target.value)}
        />

        <div className="user-home-industry-drop-down">
        <label htmlFor="options" className="user-home-industry-drop-label">Type of Company</label>
        <select id="options" value={selected} onChange={handleSelected}>
        <option value="">--Select--</option>
        <option value="tech">Technology</option>
        <option value="finance">Finance</option>
        <option value="healthcare">Healthcare</option>
        <option value="education">Education</option>
        <option value="retail">Retail</option>

        {/* ('Finance'),
('Healthcare'),
('Education'),
('Retail'),
('Manufacturing'),
('Transportation & Logistics'),
('Energy & Utilities'),
('Real Estate'),
('Government'),
('Legal'),
('Media & Entertainment'),
('Marketing & Advertising'),
('Hospitality & Travel'),
('Food & Beverage'),
('Construction'),
('Nonprofit & NGOs'),
('Agriculture'),
('Telecommunications'),
('Aerospace & Defense'),
('Insurance'),
('Biotechnology'),
('Human Resources'),
('Environmental Services'),
('Automotive'),
('Sports & Recreation'),
('Consumer Goods'),
('E-commerce'),
('Arts & Design'),
('Cybersecurity'); */}

        <option value="view">View Company</option>
        </select>
        </div>

        </div>
        <div>
        <button className="next-button-">Next</button>
        </div>
        </form>
        )}
        {currentStage=== FORM_STAGES.INDUSTRY && (<form onSubmit={handleBioNextButton}>
        <div>
        <button onClick={(e) => setCurrentStage(FORM_STAGES.FULL_NAME)} className="back-button">Back</button>
        <div className="user-home-industry-drop-down">
        <label htmlFor="options" className="user-home-industry-drop-label">Industry</label>
        <select id="options" value={selectedIndustry} onChange={handleSelected}>
        <option value="">--Select--</option>
        <option value="public">Public Company</option>
        <option value="self-employed">Self-Employed</option>

        <option value="gov-agency">Government Agency</option>
        <option value="non-profit">Non-Profit</option>
        <option value="sole-proprietorship">Sole Proprietorship</option>
        <option value="privately-held">Privately Held</option>
        <option value="partnership">Partnership</option>
        </select>
        </div>


        <button>Upload Company Logo</button>
        </div>
        {/* <div>
        <input
        className="last-name-registration-box"
        type="text"
        placeholder="Enter Last Name"
        value={lastName}
        onChange={(e)=> setLastName(e.target.value)}
        />
        </div> */}
        <div>
        <button className="next-bio-button">Next</button>
        </div>
        </form>
        )}
        {currentStage=== FORM_STAGES.LOCATION && (<form onSubmit={handleCreateButton}>
        <div className="location-register-container">
        <input
        className="location-country-registration-box"
        type="text"
        placeholder="Enter Country of Residence"
        value={country}
        onChange={(e) => setCountry(e.target.value)}
        />
        </div>
        <div>
        <input
        className="location-city-registration-box"
        type="text"
        placeholder="Enter City of Residence"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        />
        </div>
        <div>

        <input
        className="location-state-registration-box"
        type="text"
        placeholder="Enter State of Residence"
        value={state}
        onChange={(e) => setState(e.target.value)}
        />
        </div>
        {/* <div>
        <input
        className="last-name-registration-box"
        type="text"
        placeholder="Enter Last Name"
        value={lastName}
        onChange={(e)=> setLastName(e.target.value)}
        />
        </div> */}
        <div>
        <button className="create-profile-button">Create Profile</button>
        </div>
        </form>
        )}
        </div>
        </>
    )
}

export default CompanyProfileRegistration;
