import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router-dom";
import { useSearchParams } from "react-router-dom";


const SearchPage = ({fetchUsersByName,fetchJobsByName,fetchCompaniesByName,storage,fetchUser,fetchJob}) => {
    // const [searchQuery] = useSearchParams;
    // const query = searchQuery.get("query") || "";
    const [results,setResults] = useState([]);
    const [params] = useSearchParams();
    const [selected,setSelected] = useState('users');
    const firstName = params.get('first_name') || '';
    const lastName = params.get('last_name') || '';
    const [fullName,setFullName] = useState(firstName);

    const navigate = useNavigate();

    useEffect(() => {
      if (firstName || lastName) {
        fetchResults();
      }
    }, []); // â† dependencies here


    const fetchResults = async() => {

      const response = await fetchUsersByName(firstName.trim(),lastName.trim());
      setResults(response);

    }

    const getUser = async(userId) => {
      const response = await fetchUser(userId);
      storage.setItem("otherUser",response.user_id);
      if (response.first_name){
        navigate(`/signed-in/feed/user/${userId}`);
      }
    }
    const getJob = async(jobId) => {
      const response = await fetchJob(jobId);
      storage.setItem("jobId",response.id);
      if (response.title){
        navigate(`/signed-in/feed/job/${jobId}`);
      }
    }


    const fetchNewResults = async(first,last) => {


      const response = await fetchUsersByName(first.trim(),last.trim());
      setResults(response);

    }

    const fetchNewJobResults = async(first,last) => {


      const response = await fetchJobsByName(first.trim(),last.trim());
      setResults(response);

    }

    const fetchNewCompanyResults = async(first,last) => {


      const response = await fetchCompaniesByName(first.trim(),last.trim());
      setResults(response);

    }

    const handleSearchUser = (e) => {
      e.preventDefault();
      // setFullName(value);
      // if (!fullName.trim()){
      //     alert("Search can't be empty");
      //     return;
      // }
      const name = fullName ?? "";
      const trimmed = name?.trim();
      console.log(trimmed);

      const spaceIdx = trimmed.indexOf(" ");
      console.log("idx: ",spaceIdx);
      if (selected === "users"){
        if (spaceIdx !== -1){
            const firstName = (trimmed.slice(0,spaceIdx));
            const lastName =(trimmed.slice(spaceIdx+1));
            console.log("firstname: ",firstName);
            fetchNewResults(firstName,lastName);
        }
        else{
            const firstName = trimmed;
            const lastName = "";
            fetchNewResults(firstName,lastName);
            // setLastName("");

        }
      }
      else if (selected === "jobs"){
        // if (spaceIdx !== -1){
        //     const firstName = (trimmed.slice(0,spaceIdx));
        //     const lastName =(trimmed.slice(spaceIdx+1));
        //     console.log("firstname: ",firstName);
        //     fetchNewJobResults(firstName,lastName);
        // }
        // else{
            const firstName = trimmed;
            const lastName = "";
            fetchNewJobResults(firstName,lastName);
            // setLastName("");

        // }
      }
      else if (selected === "companies"){
        //   // if (spaceIdx !== -1){
        //     const firstName = (trimmed.slice(0,spaceIdx));
        //     const lastName =(trimmed.slice(spaceIdx+1));
        //     console.log("firstname: ",firstName);
        //     fetchNewCompanyResults(firstName,lastName);
        // }
        // // else{
            const firstName = trimmed;
            const lastName = "";
            fetchNewCompanyResults(firstName,lastName);
            // setLastName("");


      }

  }


      return (
        <>
        <div>
        <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
        {selected === "users" && ( <div>
            {results.map((result) => (
                <li key={result.user_id} className="home-search-result-list">
                <button className="home-search-result-list-button" onClick={(e)=>getUser(result.user_id)}><p>{result.first_name} {result.last_name}</p></button>
                </li>
            ))}
            </div>
          )}
        {selected === "jobs" && ( <div>
            {results.map((result) => (
                <li key={result.id} className="home-search-job-result-list">
                <button className="home-search-job-result-list-button" onClick={(e)=>getJob(result.id)}>
                  <h1>Company: {result.company_name}</h1>
                  <h2>Job Position: {result.title} </h2>
                <h2>Salary: ${result.salary}/hr</h2>
                </button>
                </li>
            ))}
            </div>
          )}
        {selected === "companies" && ( <div>
            {results.map((result) => (
                <li key={result.id} className="home-search-job-result-list">
                <button className="home-search-job-result-list-button" onClick={(e)=>getUser(result.id)}><p>{result.company_name} {result.industry}</p>
                <p>Number of Jobs Posted: {result.number_of_jobs}</p>
                </button>
                </li>
            ))}
            </div>
          )}
            <form onSubmit={handleSearchUser}>
      <select className="" value={selected} onChange={(e) => setSelected(e.target.value)}>
      <option value="users">Users</option>
      <option value="jobs">Jobs</option>
      <option value="companies">Companies</option>
      </select>
      <input
        className="user-page-search-button"
        type="text"
        placeholder="Search ðŸ”"
        value={fullName}
        onChange={(e) => setFullName(e.target.value)} 
        />
      </form>
        </div>
        </>
      )

            }
export default SearchPage;
