import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router";
import PostModal from "./PostModal";
import "./childComponents.css";
import image from "./../../images/profile_pic.png";

const MyUserProfile = ({storage,fetchUser,fetchAllSkills,fetchUserSkills,fetchUserExperience,addUserSkills,addUserWorkExperience}) => {
const [currUser,setCurrUser] = useState('');
const [allSkills,setAllSkills] = useState([]);
const [allWorkExperience,setAllWorkExperience] = useState([]);
const [fetchedSkills,setFetchedSkills] = useState([]);
const [mySkills,setMySkills] = useState([]);
const [workTitle,setWorkTitle] = useState('');
const [workDescription,setWorkDescription] = useState('');
const [companyName,setCompanyName] = useState('');
const [workSkillsFrontEnd,setWorkSkillsFrontEnd] = useState([]);
const [workSkills,setWorkSkills] = useState([]);
const [workType,setWorkType] = useState('');
const [addSkillsButton,setAddSkillsButton] = useState(false);
const [locationState,setLocationState] = useState('')
const [locationCountry,setLocationCountry] = useState('')
const [showModal,setShowModal] = useState(false);
const [showModalWorkExperience,setShowModalWorkExperience] = useState(false);

const navigate = useNavigate();
// console.log(currUser.first_name);

const isOpen = () =>  setShowModal(true);
const isClosed = () => setShowModal(false);

const isOpenWork = () =>  setShowModalWorkExperience(true);
const isClosedWork = () => setShowModalWorkExperience(false);

useEffect(() => {

    getCurrUser();
    getSkills();
    getExperience();
},[])

const getCurrUser = async() => {
    const response = await fetchUser(storage.getItem("currUserId"));
    setCurrUser(response);
    storage.setItem("currUserProfile",response);
    console.log(response);
  }

const getSkills = async() => {
    const response = await fetchAllSkills();
    setAllSkills(response);
    const response_two = await fetchUserSkills();
    setFetchedSkills(response_two);

}

const getExperience = async() => {
    const response = await fetchUserExperience();
    setAllWorkExperience(response);

}


const handleAddSkills = async(skill) => {
    setMySkills((prevSkills) => [...prevSkills,skill]);
    const updatedSkills = allSkills.filter(
        (skill_item) => skill_item.id !== skill.id
    )
    setAllSkills(updatedSkills);
    console.log(mySkills);

}

const handleSubmitNewSkills = async(e) => {
    e.preventDefault();
    const response = addUserSkills(mySkills);
    if (response){
        setFetchedSkills(prevSkills => [...prevSkills,...mySkills]);
        isClosed();
    }

}

const handleWorkType = (e) => {
    const value = e.target.value;
    setWorkType(value);
}

const handleSelectedCountry = (e) => {
    // e.preventDefault();
    const value = e.target.value;
    setLocationCountry(value);
    // if (selected === ''){
    //     alert("The Company Type is required");
    //     return;
    // }
}

const handleSelectedState = (e) => {
    // e.preventDefault();
    const value = e.target.value;
    setLocationState(value);
    // if (selected === ''){
    //     alert("The Company Type is required");
    //     return;
    // }
}

const handleAddSkillsTwo = async(skill) => {
    setWorkSkills((prevSkills) => [...prevSkills,skill]);
    const updatedSkills = allSkills.filter(
        (skill_item) => skill_item.id !== skill.id
    )
    setAllSkills(updatedSkills);
    console.log(mySkills);

}

const handleSubmitNewWorkExperience = async(e) => {
    e.preventDefault();

    const fullJobDetails = {
        company_name: companyName.trim(),
        title: workTitle.trim(),
        description: workDescription.trim(),
        job_type: workType.trim(),
        location_state: locationState.trim(),
        location_country: locationCountry.trim(),
        skills: workSkills
    }

    const response = addUserWorkExperience(fullJobDetails);
    if (response){
        setFetchedSkills(prevSkills => [...prevSkills,...mySkills]);
        isClosedWork();
    }

}

return (
    <>
    <div>
    <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
    <div className="user-profile-name-profile-pic-container">
    <img className="user-profile-image-screen" src={image}></img>
    <h2>{currUser.first_name} {currUser.last_name}</h2>
    {currUser.location_country && currUser.location_state && (
    <h2 className="user-profile-location-screen">{currUser.location_state}, {currUser.location_country}</h2>
    )}
    {currUser.location_country && !currUser.location_state && (
    <h2 className="user-profile-location-screen">{currUser.location_country}</h2>
    )}
    {!currUser.location_country && !currUser.location_state && (
    <button className="user-profile-location-screen">Add Location</button>
    )}

    </div>
    <div>
    <p>
    <h1>Work Experience</h1>
    <button onClick={isOpenWork}> Add</button>
    {allWorkExperience.map((WorkExperience) => (
        <div>
        <p>
        {/* <button className="my-skills-my-profile-no-modal-button">{WorkExperience.company_name}</button> */}
        <label>Company Name:</label>
        <h4>{WorkExperience.company_name}</h4>
        <label>Job Title:</label>
        <h5>{WorkExperience.title} :</h5>
        <label>Job Description:</label>
        <h5>{WorkExperience.description}</h5>
        </p>
        </div>
    ))}
    </p>
    <PostModal isOpen={showModalWorkExperience} onClose={isClosedWork}>
    <h2>Add Work Experience</h2>
    <label>Company or Orgnaization</label>
    <input
    type="text"
    placeholder="Enter Company of Job"
    value={companyName}
    onChange={(e) => setCompanyName(e.target.value)}/>
    <label>Job Title</label>
    <input
    type="text"
    placeholder="Type Job Title"
    value={workTitle}
    onChange={(e) => setWorkTitle(e.target.value)}/>
    <label>Job Type</label>
    <select id="options" value={workType} onChange={(e) => handleWorkType(e)}>
        <option value="" disabled>--Select--</option>
        <option value="part-time">Part-Time</option>
        <option value="full-time">Full-Time</option>
        <option value="internship">Internship</option>
        <option value="seasonal">Seasonal</option>
        <option value="contract">Contract</option>
    </select>
    {locationCountry === "United States" && (
            <div>
                <select id="options" value={locationState} onChange={(e) => handleSelectedState(e)}>
                <option value="" disabled>--Select State--</option>
                <option value="Alabama">Alabama</option>
                <option value="Alaska">Alaska</option>
                <option value="Arizona">Arizona</option>
                <option value="Arkansas">Arkansas</option>
                <option value="California">California</option>
                <option value="Colorado">Colorado</option>
                <option value="Connecticut">Connecticut</option>
                <option value="Delaware">Delaware</option>
                <option value="Florida">Florida</option>
                <option value="Georgia">Georgia</option>
                <option value="Hawaii">Hawaii</option>
                <option value="Idaho">Idaho</option>
                <option value="Illinois">Illinois</option>
                <option value="Indiana">Indiana</option>
                <option value="Iowa">Iowa</option>
                <option value="Kansas">Kansas</option>
                <option value="Kentucky">Kentucky</option>
                <option value="Louisiana">Louisiana</option>
                <option value="Maine">Maine</option>
                <option value="Maryland">Maryland</option>
                <option value="Massachusetts">Massachusetts</option>
                <option value="Michigan">Michigan</option>
                <option value="Minnesota">Minnesota</option>
                <option value="Mississippi">Mississippi</option>
                <option value="Missouri">Missouri</option>
                <option value="Montana">Montana</option>
                <option value="Nebraska">Nebraska</option>
                <option value="Nevada">Nevada</option>
                <option value="New-Hampshire">New Hampshire</option>
                <option value="New-Jersey">New Jersey</option>
                <option value="New-Mexico">New Mexico</option>
                <option value="New-York">New York</option>
                <option value="North-Carolina">North Carolina</option>
                <option value="North-Dakota">North Dakota</option>
                <option value="Ohio">Ohio</option>
                <option value="Oklahoma">Oklahoma</option>
                <option value="Oregon">Oregon</option>
                <option value="Pennsylvania">Pennsylvania</option>
                <option value="Rhode-Island">Rhode Island</option>
                <option value="South-Carolina">South Carolina</option>
                <option value="South-Dakota">South Dakota</option>
                <option value="Tennessee">Tennessee</option>
                <option value="Texas">Texas</option>
                <option value="Utah">Utah</option>
                <option value="Vermont">Vermont</option>
                <option value="Virginia">Virginia</option>
                <option value="Washington">Washington</option>
                <option value="West-Virginia">West Virginia</option>
                <option value="Wisconsin">Wisconsin</option>
                <option value="Wyoming">Wyoming</option>
                </select>
            </div>
        )}
    <label>Job Description</label>
    <textarea
    type="text"
    placeholder="Enter Job Description"
    value={workDescription}
    onChange={(e) => setWorkDescription(e.target.value)}
    />
            <div className="location-register-container">
            <label>Location: </label>
            <select id="options" value={locationCountry} onChange={(e) => handleSelectedCountry(e)}>
            <option value="" disabled>--Select Country--</option>
            <option value="United States">United States</option>
            <option value="United Kingdom">United Kingdom</option>
            <option value="Canada">Canada</option>
            <option value="Mexico">Mexico</option>
            </select>
            </div>
    <input
    type="text"
    placeholder="Type Job Title"
    value={workTitle}
    onChange={(e) => setWorkTitle(e.target.value)}/>
    {addSkillsButton === false && (
        <div>
        <h3>Skills</h3>
        <button onClick={(e) => setAddSkillsButton(true)}> Add</button>
        </div>
    )}
    {addSkillsButton === true && (
        <div>
        {workSkills.length > 0 && (
            <div>
                <h3>Selected Work Skills</h3>
                <p className="skills-my-profile-modal">

                {workSkills.map((skill) => (
                    <div>
                    <button className="skills-my-profile-button" > {skill.skill_name}</button>
                    </div>
                ))}
                </p>
            </div>
        )}
        <h3>Select Skills</h3>
        <p className="skills-my-profile-modal">

        {allSkills.map((skill) => (
            <div>
            <button className="skills-my-profile-button" onClick={(e) => (handleAddSkillsTwo(skill))}>+ {skill.skill_name}</button>
            </div>
        ))}
        </p>
        </div>
