import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router-dom";
import { useSearchParams } from "react-router-dom";


const SearchPage = ({fetchUsersByName,storage,fetchUser}) => {
    // const [searchQuery] = useSearchParams;
    // const query = searchQuery.get("query") || "";
    const [results,setResults] = useState([]);

    const [params] = useSearchParams();
    const firstName = params.get('first_name') || '';
    const lastName = params.get('last_name') || '';
    const navigate = useNavigate();

    useEffect(() => {
      if (firstName || lastName) {
        fetchResults();
      }
    }, [firstName, lastName]); // ← dependencies here


    const fetchResults = async() => {

      const response = await fetchUsersByName(firstName.trim(),lastName.trim());
      setResults(response);

    }

    const getUser = async(userId) => {
      const response = await fetchUser(userId);
      if (response.first_name){
        navigate(`/signed-in/feed/user/${userId}`);
      }
    }

      return (
        <>
        <div>
        <button onClick={(e) => navigate("/signed-in/feed/") } className="back-button">Back</button>
            {results.map((result) => (
                <li key={result.user_id} className="home-search-result-list">
                <button className="home-search-result-list-button" onClick={getUser(result.user_id)}><p>{result.first_name} {result.last_name}</p></button>
                </li>
            ))}
        </div>
        </>
      )

            }
export default SearchPage;
